import React, {useState} from "react";
import Card from "../Ui/Card";
import classes from './AddUser.module.css'
import Button from "../Ui/Button";
import ErrorModal from "../Ui/ErrorModal";

const AddUser = (props) => {
    const [enteredUserName, setEnteredUserName] = useState('')
    const [enteredAge, setEnteredAge] = useState('')

    const AddUserHandler = (event) =>{
        event.preventDefault();
        
        if(enteredUserName.trim().length === 0 || enteredAge.trim().length === 0 || +enteredAge < 1){
            return;
        }
        // if(enteredAge < 1){
        //     return;
        // }
        // console.log(enteredUserName, enteredAge)
        props.onAddUser(enteredUserName, enteredAge);
       

        setEnteredUserName('')
        setEnteredAge('')
    }

    const userNameChangeHandler = (event) => {
        setEnteredUserName(event.target.value)
    }

    const ageChangeHandler = (event) => [
        setEnteredAge(event.target.value)
    ]

    return(
       <div>
          <ErrorModal tit/>
            <Card className ={classes.input}>
                    <form action="" onSubmit={AddUserHandler}>
                        <label htmlFor="username">Username</label>
                        <input id="username" value={enteredUserName} type="text" onChange={userNameChangeHandler}/>
                        <label htmlFor="username">Age (Years)</label>
                        <input id="username" value={enteredAge} type="number" min={0} onChange={ageChangeHandler}/>
                        <Button type="submit">Submit</Button>
                    </form>
            </Card>
       </div>
    )
}

export default AddUser;
